parameters.jsCode:
// Ambil markdown dari input, misalnya dari field body atau text
const markdown = $input.first().json.content.parts[0].text

// Ekstrak semua URL gambar dari ![Gambar Artikel](...) menggunakan regex
const regex = /!\[.*?\]\((https?:\/\/[^\s)]+)\)/g;

let matches;
const imageUrls = [];

while ((matches = regex.exec(markdown)) !== null) {
  imageUrls.push(matches[1]); // Push hanya URL-nya
}

// Format output agar bisa digunakan di node selanjutnya
return imageUrls.map(url => ({
  json: { url_gambar: url }
}));
